pp.example:
			(C#) how to register pluging command line prefix and plugin menu command
			
				
public override void Connect()
{
	Far.RegisterPrefix([your prefix], [method to be called]);
	this.menuItem = Far.CreatePluginsMenuItem();
	this.menuItem.Name = [name of plugin menu item];
	this.menuItem.OnOpen += [method to be called];
	Far.RegisterPluginsMenuItem(this.menuItem);
	...
}
